import React, { useState } from "react";
import { useNavigate } from "react-router-dom"; // useNavigate 임포트
import "./style.css"; // CSS 파일 경로

const Group = ({ groups }) => {
  const [filter, setFilter] = useState("all"); // 필터 상태 (all, public, private)
  const navigate = useNavigate(); // useNavigate 훅 사용

  const handleFilterClick = (filterType) => {
    setFilter(filterType);

    // 클릭 시 경로 변경
    if (filterType === "public") {
      navigate("/public-group-list"); // 공개 그룹 경로로 이동
    } else if (filterType === "private") {
      navigate("/private-group-list"); // 비공개 그룹 경로로 이동
    }
  };

  const filteredGroups = groups.filter((group) => {
    if (filter === "public") return !group.isPrivate;
    if (filter === "private") return group.isPrivate;
    return true; // "all"일 경우 모든 그룹을 반환
  });

  return (
    <div className="group-list-page">
      <div className="filter-buttons">
        <button onClick={() => handleFilterClick("public")}>공개</button>
        <button onClick={() => handleFilterClick("private")}>비공개</button>
      </div>
      <div className="group-list">
        {filteredGroups.map((group, index) => (
          <div key={index} className="group-item">
            <img src={group.image} alt={group.title} />
            <h3>{group.title}</h3>
            <p>{group.description}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Group;
