import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { v4 as uuidv4 } from "uuid"; // uuid import
import "./style.css";

const CreateGroupPage = ({ addGroup }) => {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [image, setImage] = useState("");
  const [isPrivate, setIsPrivate] = useState(false);
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImage(URL.createObjectURL(file)); // 선택된 파일의 URL을 상태로 저장
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const newGroup = {
      id: uuidv4(), // 새로운 그룹에 고유한 id 할당
      title,
      description,
      image,
      isPrivate,
      password,
      createdAt: new Date(), // 그룹 생성 시간도 추가
      likes: 0, // 기본값으로 likes를 0으로 설정
      memories: [],
      memoriesCount: 0,
      recommendations: 0,
    };
    addGroup(newGroup);
    navigate("/");
  };

  return (
    <div className="create-group-container">
      <h1 className="title">그룹 만들기</h1>
      <form onSubmit={handleSubmit} className="create-group-form">
        <div className="form-group">
          <label>그룹명</label>
          <input
            type="text"
            placeholder="그룹명을 입력해 주세요"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label>대표 이미지</label>
          <div className="file-input-container">
            <input
              type="file"
              accept="image/*"
              onChange={handleFileChange} // 파일 선택 시 handleFileChange 호출
              className="file-input"
            />
          </div>
        </div>
        <div className="form-group">
          <label>그룹 소개</label>
          <textarea
            placeholder="그룹을 소개해 주세요"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label>그룹 공개 선택</label>
          <div className="toggle-container">
            <label className="toggle-label">
              공개
              <input
                type="checkbox"
                checked={!isPrivate}
                onChange={(e) => setIsPrivate(!e.target.checked)}
                className="toggle-input"
              />
              <span className="toggle-slider"></span>
            </label>
          </div>
        </div>
        {isPrivate && (
          <div className="form-group">
            <label>비밀번호</label>
            <input
              type="password"
              placeholder="비밀번호를 입력해 주세요"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
        )}
        <button type="submit" className="submit-button">
          만들기
        </button>
      </form>
    </div>
  );
};

export default CreateGroupPage;
