import React, { useState } from "react";
import { useNavigate, useParams } from "react-router-dom";

const Access = ({ privateGroups }) => {
  const [inputPassword, setInputPassword] = useState("");
  const navigate = useNavigate();
  const { id } = useParams();

  const selectedGroup = privateGroups.find((group) => group.id === id);

  const handlePasswordSubmit = (e) => {
    e.preventDefault();
    if (selectedGroup && inputPassword === selectedGroup.password) {
      navigate(`/private-group/${selectedGroup.id}`); // 비밀번호가 맞으면 그룹 상세 페이지로 이동
    } else {
      alert("비밀번호가 틀렸습니다.");
    }
  };

  if (!selectedGroup) {
    return <p>그룹을 찾을 수 없습니다.</p>;
  }

  return (
    <div className="private-group-access">
      <div className="private-group-access-content">
        <h2>비공개 그룹</h2>
        <p>비공개 그룹에 접근하기 위해선 확인이 필요합니다.</p>
        <form onSubmit={handlePasswordSubmit}>
          <input
            type="password"
            placeholder="비밀번호를 입력해 주세요"
            value={inputPassword}
            onChange={(e) => setInputPassword(e.target.value)}
          />
          <button type="submit">제출하기</button>
        </form>
      </div>
    </div>
  );
};

export default Access;
