import React, { useState } from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import GroupList from "./screens/GroupList";
import CreateGroupPage from "./screens/CreateGroupPage";
import PrivateGroupPage from "./screens/PrivateGroupPage"; // 업데이트된 경로

const App = () => {
  const [groups, setGroups] = useState([]);

  const addGroup = (newGroup) => {
    setGroups([...groups, newGroup]);
  };

  const privateGroups = groups.filter((group) => group.isPrivate);

  return (
    <Router>
      <Routes>
        <Route path="/" element={<GroupList groups={groups} />} />
        <Route
          path="/create"
          element={<CreateGroupPage addGroup={addGroup} />}
        />
        <Route
          path="/private-group-list"
          element={<PrivateGroupPage privateGroups={privateGroups} />}
        />
      </Routes>
    </Router>
  );
};

export default App;
