import React, { useState } from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import Group from "./screens/Group";
import CreateGroupPage from "./screens/CreateGroup";
import PrivateGroupAccessPage from "./screens/PrivateAccess/index";
import GroupDetail from "./screens/GroupDetail";
import Layout from "./Layout/Layout";

const App = () => {
  // 테스트용 그룹 데이터 추가
  const [groups, setGroups] = useState([
    {
      id: 1,
      title: "추억의 그룹",
      description: "우리가 함께한 추억을 기록하는 그룹입니다.",
      image: "https://via.placeholder.com/150",
      isPrivate: false,
      memoryStreak: 7, // 7일 연속 추억 등록 조건 충족
      memoryCount: 25,
      createdAt: "2022-08-01", // 1년이 지나지 않음
      likeCount: 5000,
      memories: [
        { id: 1, content: "첫 번째 추억", likeCount: 1000 },
        { id: 2, content: "두 번째 추억", likeCount: 2000 },
        // 기타 추억 데이터...
      ],
      badgeIds: [], // 이 부분은 `Group` 컴포넌트에서 자동으로 채워짐
    },
  ]);

  const addGroup = (newGroup) => {
    setGroups([...groups, newGroup]);
  };

  const publicGroups = groups.filter((group) => !group.isPrivate);
  const privateGroups = groups.filter((group) => group.isPrivate);

  return (
    <Router>
      <Routes>
        <Route element={<Layout />}>
          {" "}
          {/* 공통 레이아웃 적용 */}
          <Route path="/" element={<Group groups={groups} />} />
          <Route
            path="/create"
            element={<CreateGroupPage addGroup={addGroup} />}
          />
          <Route
            path="/public-group-list"
            element={<Group groups={publicGroups} />}
          />
          <Route
            path="/private-access/:id"
            element={<PrivateGroupAccessPage privateGroups={privateGroups} />}
          />
          <Route
            path="/private-group-list"
            element={<Group groups={privateGroups} />}
          />
          <Route
            path="/group-details/:id"
            element={<GroupDetail groups={groups} />}
          />
        </Route>
      </Routes>
    </Router>
  );
};

export default App;
