import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import Badge from "../Badge";
import Modal from "react-modal";
import "./style.css";

const GroupDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [group, setGroup] = useState(null);
  const [likeCount, setLikeCount] = useState(0);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isDeleteModalOpen, setIsDeleteModalOpen] = useState(false);
  const [groupName, setGroupName] = useState("");
  const [groupDescription, setGroupDescription] = useState("");
  const [groupImage, setGroupImage] = useState("");
  const [isPrivate, setIsPrivate] = useState(false);
  const [password, setPassword] = useState("");
  const [deletePassword, setDeletePassword] = useState("");

  useEffect(() => {
    // 그룹 데이터를 서버에서 가져오는 함수
    const fetchGroup = async () => {
      try {
        const response = await fetch(`/api/groups/${id}`);
        if (!response.ok) {
          throw new Error("그룹 정보를 불러오는데 실패했습니다.");
        }
        const data = await response.json();
        setGroup(data);
        setLikeCount(data.likeCount);
        setGroupName(data.title);
        setGroupDescription(data.description);
        setGroupImage(data.image);
        setIsPrivate(data.isPrivate);
      } catch (error) {
        console.error("Error fetching group:", error);
      }
    };

    fetchGroup();
  }, [id]);

  if (!group) {
    return <p>그룹을 찾을 수 없습니다.</p>;
  }

  const handleLike = async () => {
    try {
      const response = await fetch(`/api/groups/${id}/like`, {
        method: "POST",
      });
      if (!response.ok) {
        throw new Error("공감 보내기에 실패했습니다.");
      }
      setLikeCount(likeCount + 1);
    } catch (error) {
      console.error("Error liking group:", error);
    }
  };

  const handleSaveChanges = async () => {
    if (password === group.password) {
      const updatedGroup = {
        title: groupName,
        description: groupDescription,
        image: groupImage,
        isPrivate,
      };

      try {
        const response = await fetch(`/api/groups/${id}`, {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(updatedGroup),
        });

        if (!response.ok) {
          throw new Error("그룹 정보 수정에 실패했습니다.");
        }

        setGroup(updatedGroup);
        handleCloseModal();
      } catch (error) {
        console.error("Error updating group:", error);
        alert("그룹 정보 수정에 실패했습니다.");
      }
    } else {
      alert("비밀번호가 일치하지 않습니다. 다시 시도해 주세요.");
    }
  };

  const handleDeleteGroup = async () => {
    if (deletePassword === group.password) {
      try {
        const response = await fetch(`/api/groups/${id}`, {
          method: "DELETE",
        });

        if (!response.ok) {
          throw new Error("그룹 삭제에 실패했습니다.");
        }

        alert("그룹이 성공적으로 삭제되었습니다.");
        navigate("/");
      } catch (error) {
        console.error("Error deleting group:", error);
        alert("그룹 삭제에 실패했습니다.");
      }
    } else {
      alert("비밀번호가 일치하지 않습니다. 다시 시도해 주세요.");
    }
  };

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => setGroupImage(reader.result);
      reader.readAsDataURL(file);
    }
  };

  const handleOpenModal = () => setIsModalOpen(true);
  const handleCloseModal = () => setIsModalOpen(false);
  const handleOpenDeleteModal = () => setIsDeleteModalOpen(true);
  const handleCloseDeleteModal = () => setIsDeleteModalOpen(false);

  return (
    <div className="group-detail-container">
      <div className="group-detail-header">
        <img src={group.image} alt={group.title} className="group-image" />
        <div className="group-info">
          <div className="group-meta-actions">
            <div className="group-meta">
              <span>D+{group.dDay}</span>
              <span> | </span>
              <span>{group.isPrivate ? "비공개" : "공개"}</span>
            </div>
            <div className="group-actions">
              <button className="edit-group-button" onClick={handleOpenModal}>
                그룹 정보 수정하기
              </button>
              <button
                className="delete-group-button"
                onClick={handleOpenDeleteModal}
              >
                그룹 삭제하기
              </button>
            </div>
          </div>
          <h1>{group.title}</h1>
          <div className="group-stats-inline">
            <span>추억 {group.memoryCount}</span>
            <span>그룹 공감 {likeCount}</span>
          </div>
          <p>{group.description}</p>
        </div>
      </div>

      <div className="group-badges">
        <div className="badge-list">
          <span>획득 배지</span>
          <span></span>
        </div>
        <Badge badgeIds={group.badgeIds} />
        <button className="like-button" onClick={handleLike}>
          공감 보내기
        </button>
      </div>

      <div className="memories-list">
        <div className="memories-header">
          <h2>추억 목록</h2>
          <button className="upload-button">추억 올리기</button>
        </div>
        {group.memories && group.memories.length > 0 ? (
          group.memories.map((memory, index) => (
            <div key={index} className="memory-item">
              <p>공감 수: {memory.likeCount}</p>
              {/* 필요에 따라 다른 메모리 정보를 여기에 추가할 수 있음 */}
            </div>
          ))
        ) : (
          <p>게시된 추억이 없습니다.</p>
        )}
      </div>

      {/* 그룹 정보 수정 모달 */}
      <Modal
        isOpen={isModalOpen}
        onRequestClose={handleCloseModal}
        contentLabel="그룹 정보 수정"
        className="group-modal"
        overlayClassName="group-modal-overlay"
      >
        <h2>그룹 정보 수정</h2>
        <button onClick={handleCloseModal} className="close-modal-button">
          X
        </button>
        <form className="edit-group-form">
          <label>그룹명</label>
          <input
            type="text"
            value={groupName}
            onChange={(e) => setGroupName(e.target.value)}
          />
          <label>대표 이미지</label>
          <div className="image-upload">
            <input type="text" value={groupImage} readOnly />
            <button
              type="button"
              className="file-select-button"
              onClick={() => document.querySelector("#fileInput").click()}
            >
              파일 선택
            </button>
            <input
              id="fileInput"
              type="file"
              style={{ display: "none" }}
              onChange={handleFileChange}
            />
          </div>
          <label>그룹 소개</label>
          <textarea
            value={groupDescription}
            onChange={(e) => setGroupDescription(e.target.value)}
          />
          <label>그룹 공개 선택</label>
          <div className="toggle-switch">
            <input
              type="checkbox"
              checked={isPrivate}
              onChange={() => setIsPrivate(!isPrivate)}
            />
            <span>{isPrivate ? "비공개" : "공개"}</span>
          </div>
          <label>수정 권한 인증</label>
          <input
            type="password"
            placeholder="비밀번호를 입력해 주세요"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <button
            type="button"
            className="save-button"
            onClick={handleSaveChanges}
          >
            수정하기
          </button>
        </form>
      </Modal>

      {/* 그룹 삭제 모달 */}
      <Modal
        isOpen={isDeleteModalOpen}
        onRequestClose={handleCloseDeleteModal}
        contentLabel="그룹 삭제 확인"
        className="group-modal"
        overlayClassName="group-modal-overlay"
      >
        <h2>그룹 삭제</h2>
        <button onClick={handleCloseDeleteModal} className="close-modal-button">
          X
        </button>
        <form className="delete-group-form">
          <label>삭제 권한 인증</label>
          <input
            type="password"
            placeholder="비밀번호를 입력해 주세요"
            value={deletePassword}
            onChange={(e) => setDeletePassword(e.target.value)}
          />
          <button
            type="button"
            className="delete-button"
            onClick={handleDeleteGroup}
          >
            삭제하기
          </button>
        </form>
      </Modal>
    </div>
  );
};

export default GroupDetail;
