import React, { useState } from "react";
import { useParams } from "react-router-dom";
import Badge from "../Badge";
import Modal from "react-modal";
import "./style.css";

const GroupDetail = ({ groups }) => {
  // URL 파라미터에서 그룹 ID를 가져옴
  const { id } = useParams();
  // 그룹 목록에서 해당 ID를 가진 그룹을 찾음
  const group = groups.find((group) => group.id === id);

  // 상태 변수 설정: 공감 수, 모달 열림 상태, 그룹 정보 등
  const [likeCount, setLikeCount] = useState(group?.likeCount || 0);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isDeleteModalOpen, setIsDeleteModalOpen] = useState(false);
  const [groupName, setGroupName] = useState(group.title);
  const [groupDescription, setGroupDescription] = useState(group.description);
  const [groupImage, setGroupImage] = useState(group.image);
  const [isPrivate, setIsPrivate] = useState(group.isPrivate);
  const [password, setPassword] = useState("");
  const [deletePassword, setDeletePassword] = useState("");

  // 그룹이 존재하지 않을 경우 메시지 출력
  if (!group) {
    return <p>그룹을 찾을 수 없습니다.</p>;
  }

  // 공감 버튼 클릭 시 호출되는 함수
  const handleLike = () => {
    setLikeCount(likeCount + 1);
    // 여기서 서버에 공감 수를 업데이트하는 로직을 추가할 수 있습니다.
  };

  // 그룹 정보 수정 모달 열기
  const handleOpenModal = () => setIsModalOpen(true);

  // 그룹 정보 수정 모달 닫기
  const handleCloseModal = () => setIsModalOpen(false);

  // 그룹 삭제 모달 열기
  const handleOpenDeleteModal = () => setIsDeleteModalOpen(true);

  // 그룹 삭제 모달 닫기
  const handleCloseDeleteModal = () => setIsDeleteModalOpen(false);

  // 파일 선택 시 호출되는 함수
  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => setGroupImage(reader.result);
      reader.readAsDataURL(file);
    }
  };

  // 그룹 정보 저장 버튼 클릭 시 호출되는 함수
  const handleSaveChanges = async () => {
    try {
      // 서버로 그룹 수정 요청 보내기
      const response = await fetch(`/api/groups/${group.id}`, {
        method: "PUT", // 또는 'PATCH'
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          groupName,
          groupDescription,
          groupImage,
          isPrivate,
          password, // 입력된 비밀번호를 포함
        }),
      });

      const data = await response.json();

      if (response.ok) {
        // 수정 성공 시 처리
        console.log("그룹 정보 수정 성공:", data);
        alert("그룹 정보가 성공적으로 수정되었습니다.");
        handleCloseModal();
      } else {
        // 서버에서 에러가 발생한 경우 처리
        console.log("그룹 정보 수정 실패:", data.message);
        alert(`에러: ${data.message}`);
      }
    } catch (error) {
      // 네트워크 또는 다른 오류 처리
      console.error("그룹 정보 수정 에러:", error);
      alert("그룹 정보 수정에 실패했습니다.");
    }
  };

  // 그룹 삭제 버튼 클릭 시 호출되는 함수
  const handleDeleteGroup = async () => {
    try {
      // 서버로 그룹 삭제 요청 보내기
      const response = await fetch(`/api/groups/${group.id}`, {
        method: "DELETE",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          password: deletePassword, // 입력된 삭제 비밀번호를 포함
        }),
      });

      const data = await response.json();

      if (response.ok) {
        // 삭제 성공 시 처리
        console.log("그룹 삭제 성공:", data);
        alert("그룹이 성공적으로 삭제되었습니다.");
        handleCloseDeleteModal();
      } else {
        // 서버에서 에러가 발생한 경우 처리
        console.log("그룹 삭제 실패:", data.message);
        alert(`에러: ${data.message}`);
      }
    } catch (error) {
      // 네트워크 또는 다른 오류 처리
      console.error("그룹 삭제 에러:", error);
      alert("그룹 삭제에 실패했습니다.");
    }
  };

  return (
    <div className="group-detail-container">
      <div className="group-detail-header">
        <img src={group.image} alt={group.title} className="group-image" />
        <div className="group-info">
          <div className="group-meta-actions">
            <div className="group-meta">
              <span>D+{group.dDay}</span>
              <span> | </span>
              <span>{group.isPrivate ? "비공개" : "공개"}</span>
            </div>
            <div className="group-actions">
              <button className="edit-group-button" onClick={handleOpenModal}>
                그룹 정보 수정하기
              </button>
              <button
                className="delete-group-button"
                onClick={handleOpenDeleteModal}
              >
                그룹 삭제하기
              </button>
            </div>
          </div>
          <h1>{group.title}</h1>
          <div className="group-stats-inline">
            <span>추억 {group.memoryCount}</span>
            <span>그룹 공감 {likeCount}</span>
          </div>
          <p>{group.description}</p>
        </div>
      </div>

      {/* 획득 배지와 공감 보내기 버튼 */}
      <div className="group-badges">
        <div className="badge-list">
          <span>획득 배지</span>
          <span></span>
        </div>
        <Badge badgeIds={group.badgeIds} />
        <button className="like-button" onClick={handleLike}>
          공감 보내기
        </button>
      </div>

      {/* 추억 목록 */}
      <div className="memories-list">
        <div className="memories-header">
          <h2>추억 목록</h2>
          <button className="upload-button">추억 올리기</button>
        </div>
        {group.memories && group.memories.length > 0 ? (
          group.memories.map((memory, index) => (
            <div key={index} className="memory-item">
              <p>공감 수: {memory.likeCount}</p>
              {/* 필요에 따라 다른 메모리 정보를 여기에 추가할 수 있음 */}
            </div>
          ))
        ) : (
          <p>게시된 추억이 없습니다.</p>
        )}
      </div>

      {/* 그룹 정보 수정 모달 */}
      <Modal
        isOpen={isModalOpen}
        onRequestClose={handleCloseModal}
        contentLabel="그룹 정보 수정"
        className="group-modal"
        overlayClassName="group-modal-overlay"
      >
        <h2>그룹 정보 수정</h2>
        <button onClick={handleCloseModal} className="close-modal-button">
          X
        </button>
        <form className="edit-group-form">
          <label>그룹명</label>
          <input
            type="text"
            value={groupName}
            onChange={(e) => setGroupName(e.target.value)}
          />
          <label>대표 이미지</label>
          <div className="image-upload">
            <input type="text" value={groupImage} readOnly />
            <button
              type="button"
              className="file-select-button"
              onClick={() => document.querySelector("#fileInput").click()}
            >
              파일 선택
            </button>
            <input
              id="fileInput"
              type="file"
              style={{ display: "none" }}
              onChange={handleFileChange}
            />
          </div>
          <label>그룹 소개</label>
          <textarea
            value={groupDescription}
            onChange={(e) => setGroupDescription(e.target.value)}
          />
          <label>그룹 공개 선택</label>
          <div className="toggle-switch">
            <input
              type="checkbox"
              checked={isPrivate}
              onChange={() => setIsPrivate(!isPrivate)}
            />
            <span>{isPrivate ? "비공개" : "공개"}</span>
          </div>
          <label>수정 권한 인증</label>
          <input
            type="password"
            placeholder="비밀번호를 입력해 주세요"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <button
            type="button"
            className="save-button"
            onClick={handleSaveChanges}
          >
            수정하기
          </button>
        </form>
      </Modal>

      {/* 그룹 삭제 모달 */}
      <Modal
        isOpen={isDeleteModalOpen}
        onRequestClose={handleCloseDeleteModal}
        contentLabel="그룹 삭제 확인"
        className="group-modal"
        overlayClassName="group-modal-overlay"
      >
        <h2>그룹 삭제</h2>
        <button onClick={handleCloseDeleteModal} className="close-modal-button">
          X
        </button>
        <form className="delete-group-form">
          <label>삭제 권한 인증</label>
          <input
            type="password"
            placeholder="비밀번호를 입력해 주세요"
            value={deletePassword}
            onChange={(e) => setDeletePassword(e.target.value)}
          />
          <button
            type="button"
            className="delete-button"
            onClick={handleDeleteGroup}
          >
            삭제하기
          </button>
        </form>
      </Modal>
    </div>
  );
};

export default GroupDetail;
