import React, { useState } from "react";

const PrivateGroupPage = ({ privateGroups }) => {
  const [selectedGroup, setSelectedGroup] = useState(null); // 선택된 그룹
  const [inputPassword, setInputPassword] = useState("");
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  // 그룹을 클릭했을 때 실행되는 함수
  const handleGroupClick = (group) => {
    setSelectedGroup(group);
    setIsAuthenticated(false); // 새로운 그룹을 클릭할 때마다 인증 상태 초기화
    setInputPassword(""); // 비밀번호 입력 초기화
  };

  // 비밀번호 제출 시 실행되는 함수
  const handlePasswordSubmit = (e) => {
    e.preventDefault();
    if (selectedGroup && inputPassword === selectedGroup.password) {
      setIsAuthenticated(true);
    } else {
      alert("비밀번호가 틀렸습니다.");
    }
  };

  return (
    <div>
      <h1>비공개 그룹 목록</h1>
      <div className="group-list">
        {privateGroups.map((group, index) => (
          <div
            key={index}
            className="group-item"
            onClick={() => handleGroupClick(group)} // 그룹 클릭 시 선택
            style={{ cursor: "pointer" }}
          >
            <img src={group.image} alt={group.title} />
            <h3>{group.title}</h3>
            <p>{group.description}</p>
          </div>
        ))}
      </div>

      {selectedGroup && !isAuthenticated && (
        <div className="password-form">
          <h2>{selectedGroup.title} 비밀번호 입력</h2>
          <form onSubmit={handlePasswordSubmit}>
            <input
              type="password"
              placeholder="비밀번호를 입력하세요"
              value={inputPassword}
              onChange={(e) => setInputPassword(e.target.value)}
            />
            <button type="submit">제출하기</button>
          </form>
        </div>
      )}

      {selectedGroup && isAuthenticated && (
        <div className="group-details">
          <h2>{selectedGroup.title}</h2>
          <p>{selectedGroup.description}</p>
          <img src={selectedGroup.image} alt={selectedGroup.title} />
        </div>
      )}
    </div>
  );
};

export default PrivateGroupPage;
