import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./style.css"; // CSS 파일 경로가 정확한지 확인하세요

const Group = ({ groups }) => {
  const [filter, setFilter] = useState("all");
  const [searchTerm, setSearchTerm] = useState("");
  const [sortOrder, setSortOrder] = useState("latest"); // 기본 정렬 기준: 최신순
  const navigate = useNavigate();

  const handleFilterClick = (filterType) => {
    setFilter(filterType);

    if (filterType === "public") {
      navigate("/public-group-list");
    } else if (filterType === "private") {
      navigate("/private-group-list");
    }
  };

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleSortChange = (e) => {
    setSortOrder(e.target.value);
  };

  const filteredGroups = groups
    .filter((group) => {
      if (filter === "public") return !group.isPrivate;
      if (filter === "private") return group.isPrivate;
      return true;
    })
    .filter((group) =>
      group.title.toLowerCase().includes(searchTerm.toLowerCase())
    ) // 검색 기능
    .sort((a, b) => {
      if (sortOrder === "latest") {
        return new Date(b.createdAt) - new Date(a.createdAt); // 최신순
      } else if (sortOrder === "mostLiked") {
        return b.likes - a.likes; // 공감순
      }
      return 0;
    });

  return (
    <div className="group-list-page">
      <div className="search-and-sort">
        <input
          type="text"
          placeholder="그룹명을 검색해 주세요"
          value={searchTerm}
          onChange={handleSearchChange}
          className="search-input"
        />
        <select
          value={sortOrder}
          onChange={handleSortChange}
          className="sort-select"
        >
          <option value="latest">최신순</option>
          <option value="mostLiked">공감순</option>
        </select>
      </div>
      <div className="filter-buttons">
        <button onClick={() => handleFilterClick("public")}>공개</button>
        <button onClick={() => handleFilterClick("private")}>비공개</button>
        <button onClick={() => navigate("/create")}>그룹 만들기</button>{" "}
        {/* 그룹 만들기 버튼 */}
      </div>
      <div className="group-list">
        {filteredGroups.length > 0 ? (
          filteredGroups.map((group, index) => (
            <div key={index} className="group-item">
              <img src={group.image} alt={group.title} />
              <h3>{group.title}</h3>
              <p>{group.description}</p>
            </div>
          ))
        ) : (
          <p>그룹이 없습니다.</p>
        )}
      </div>
    </div>
  );
};

export default Group;
