import React from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
} from "react-router-dom";
import GroupList from "./screens/GroupList";
import CreateGroupPage from "./screens/CreateGroupPage";
import PrivateGroupPage from "./screens/PrivateGroupPage";

class App extends React.Component {
  state = {
    groups: [],
  };

  addGroup = (newGroup) => {
    this.setState((prevState) => ({
      groups: [...prevState.groups, newGroup],
    }));
  };

  render() {
    const { groups } = this.state;
    const privateGroups = groups.filter((group) => group.isPrivate);

    return (
      <Router>
        <Routes>
          <Route path="/" element={<GroupList groups={groups} />} />
          <Route
            path="/create"
            element={<CreateGroupPage addGroup={this.addGroup} />}
          />
          <Route
            path="/private-group-list"
            element={<PrivateGroupPage privateGroups={privateGroups} />}
          />
          <Route path="*" element={<Navigate to="/" />} />
        </Routes>
      </Router>
    );
  }
}

export default App;
