import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./style.css";

const CreateGroupPage = () => {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [image, setImage] = useState(null);
  const [isPrivate, setIsPrivate] = useState(false);
  const [password, setPassword] = useState("");
  const [isSuccess, setIsSuccess] = useState(null); // 성공 또는 실패를 추적하기 위한 상태
  const navigate = useNavigate();

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImage(file); // 이미지 파일을 상태에 저장
    }
  };

  const uploadImage = async () => {
    const formData = new FormData();
    formData.append("image", image);

    const response = await fetch("/api/image", {
      method: "POST",
      body: formData,
    });

    if (!response.ok) {
      throw new Error("이미지 업로드 실패");
    }

    const data = await response.json();
    return data.imageUrl; // 이미지 URL 반환
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // 그룹이 비공개인 경우 비밀번호가 입력되었는지 확인
    if (isPrivate && !password) {
      alert("비밀번호를 입력해 주세요.");
      return;
    }

    try {
      // 이미지 업로드가 필요할 경우 실행
      const imageUrl = image ? await uploadImage() : "";

      const newGroup = {
        title,
        description,
        image: imageUrl,
        isPublic: !isPrivate,
        password: isPrivate ? password : null,
      };

      const response = await fetch("/api/groups", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(newGroup),
      });

      if (!response.ok) {
        throw new Error("그룹 생성 실패");
      }

      setIsSuccess(true); // 그룹 추가가 성공하면 성공 상태를 true로 설정
    } catch (error) {
      setIsSuccess(false); // 오류가 발생하면 성공 상태를 false로 설정
    }
  };

  return (
    <div>
      {isSuccess === null && (
        <div className="create-group-container">
          <h1 className="title">그룹 만들기</h1>
          <form onSubmit={handleSubmit} className="create-group-form">
            <div className="form-group">
              <label>그룹명</label>
              <input
                type="text"
                placeholder="그룹명을 입력해 주세요"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
              />
            </div>
            <div className="form-group">
              <label>대표 이미지</label>
              <div className="file-input-container">
                <input
                  type="file"
                  accept="image/*"
                  onChange={handleFileChange} // 파일 선택 시 handleFileChange 호출
                  className="file-input"
                />
              </div>
            </div>
            <div className="form-group">
              <label>그룹 소개</label>
              <textarea
                placeholder="그룹을 소개해 주세요"
                value={description}
                onChange={(e) => setDescription(e.target.value)}
              />
            </div>
            <div className="form-group">
              <label>그룹 공개 선택</label>
              <div className="toggle-container">
                <label className="toggle-label">
                  공개
                  <input
                    type="checkbox"
                    checked={!isPrivate}
                    onChange={(e) => setIsPrivate(!e.target.checked)}
                    className="toggle-input"
                  />
                  <span className="toggle-slider"></span>
                </label>
              </div>
            </div>
            <div className="form-group">
              <label>비밀번호</label>
              <input
                type="password"
                placeholder="비밀번호를 입력해 주세요"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required={isPrivate} // 비공개인 경우 비밀번호 필수 입력
              />
            </div>
            <button type="submit" className="submit-button">
              만들기
            </button>
          </form>
        </div>
      )}
      {isSuccess !== null && (
        <div className={`message ${isSuccess ? "success" : "failure"}`}>
          <p>{isSuccess ? "그룹 만들기 성공" : "그룹 만들기 실패"}</p>
          <p className="details">
            {isSuccess
              ? "그룹이 성공적으로 등록되었습니다."
              : "그룹 등록에 실패했습니다."}
          </p>
          <button onClick={() => navigate("/")}>확인</button>
        </div>
      )}
    </div>
  );
};

export default CreateGroupPage;
