import React, { useState } from "react";
import { useNavigate, useParams } from "react-router-dom";

const PrivateGroup = ({ privateGroups }) => {
  const [selectedGroup, setSelectedGroup] = useState(null);
  const [inputPassword, setInputPassword] = useState("");
  const [isModalOpen, setIsModalOpen] = useState(false);
  const navigate = useNavigate();
  const { id } = useParams();

  const handleGroupClick = (group) => {
    setSelectedGroup(group);
    setIsModalOpen(true);
  };

  const handlePasswordSubmit = (e) => {
    e.preventDefault();
    if (selectedGroup && inputPassword === selectedGroup.password) {
      setIsModalOpen(false);
      navigate(`/private-group/${selectedGroup.id}`); // 비밀번호가 맞으면 그룹 상세 페이지로 이동
    } else {
      alert("비밀번호가 틀렸습니다.");
    }
  };

  // 현재 URL이 그룹 상세 페이지로 향할 경우, 해당 그룹을 찾아서 렌더링
  if (id) {
    const group = privateGroups.find((group) => group.id === id);
    if (!group) {
      return <p>그룹을 찾을 수 없습니다.</p>;
    }

    return (
      <div>
        <h2>{group.title}</h2>
        <p>{group.description}</p>
        <img src={group.image} alt={group.title} />
      </div>
    );
  }

  // 그룹 목록과 모달
  return (
    <div>
      <h1>비공개 그룹 목록</h1>
      <div className="group-list">
        {privateGroups.map((group, index) => (
          <div
            key={index}
            className="group-item"
            onClick={() => handleGroupClick(group)}
            style={{ cursor: "pointer" }}
          >
            <img src={group.image} alt={group.title} />
            <h3>{group.title}</h3>
            <p>{group.description}</p>
          </div>
        ))}
      </div>

      {isModalOpen && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h2>비공개 그룹</h2>
            <p>비공개 그룹에 접근하기 위해선 확인이 필요합니다.</p>
            <form onSubmit={handlePasswordSubmit}>
              <input
                type="password"
                placeholder="비밀번호를 입력해 주세요"
                value={inputPassword}
                onChange={(e) => setInputPassword(e.target.value)}
              />
              <button type="submit">제출하기</button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default PrivateGroup;
