import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import Group from "./screens/Group";
import CreateGroupPage from "./screens/CreateGroup";
import PrivateGroupAccessPage from "./screens/PrivateAccess/index";
import GroupDetail from "./screens/GroupDetail";
import Layout from "./Layout/Layout";

const App = () => {
  const [groups, setGroups] = useState([]);

  useEffect(() => {
    // API 호출: 전체 그룹 목록 가져오기
    const fetchGroups = async () => {
      try {
        const response = await fetch("/api/groups");
        const data = await response.json();
        setGroups(data.groups);
      } catch (error) {
        console.error("Error fetching groups:", error);
      }
    };

    fetchGroups();
  }, []);

  const addGroup = (newGroup) => {
    setGroups([...groups, newGroup]);
  };

  return (
    <Router>
      <Routes>
        <Route element={<Layout />}>
          <Route path="/" element={<Group groups={groups} />} />
          <Route
            path="/create"
            element={<CreateGroupPage addGroup={addGroup} />}
          />
          <Route
            path="/public-group-list"
            element={
              <Group groups={groups.filter((group) => !group.isPrivate)} />
            }
          />
          <Route
            path="/private-access/:id"
            element={<PrivateGroupAccessPage />}
          />
          <Route
            path="/private-group-list"
            element={
              <Group groups={groups.filter((group) => group.isPrivate)} />
            }
          />
          <Route path="/group-details/:id" element={<GroupDetail />} />
        </Route>
      </Routes>
    </Router>
  );
};

export default App;
