import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./style.css";

const Group = ({ groups }) => {
  const [filter, setFilter] = useState("public"); // 기본 필터를 'public'으로 설정
  const [searchTerm, setSearchTerm] = useState(""); // 검색어 상태
  const [sortOrder, setSortOrder] = useState("latest"); // 정렬 기준 상태
  const navigate = useNavigate();

  // 필터 버튼 클릭 시 호출되는 함수
  const handleFilterClick = (filterType) => {
    setFilter(filterType);

    // 경로 변경
    if (filterType === "public") {
      navigate("/public-group-list"); // 공개 그룹 경로로 이동
    } else if (filterType === "private") {
      navigate("/private-group-list"); // 비공개 그룹 경로로 이동
    }
  };

  // 검색어가 변경될 때 호출되는 함수
  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  // 정렬 기준이 변경될 때 호출되는 함수
  const handleSortChange = (e) => {
    setSortOrder(e.target.value);
  };

  // 그룹 목록 필터링 및 정렬
  const filteredGroups = groups
    .filter((group) => {
      // 공개/비공개 필터 적용
      if (filter === "public") return !group.isPrivate;
      if (filter === "private") return group.isPrivate;
      return !group.isPrivate;
    })
    .filter((group) =>
      group.title.toLowerCase().includes(searchTerm.toLowerCase())
    ) // 검색어로 필터링
    .sort((a, b) => {
      // 정렬 기준에 따라 정렬
      if (sortOrder === "latest") {
        return new Date(b.createdAt) - new Date(a.createdAt); // 최신순
      } else if (sortOrder === "mostLiked") {
        return b.likes - a.likes; // 공감순
      }
      return 0;
    });

  return (
    <div className="group-list-page">
      <div className="search-and-sort">
        <div className="filter-buttons">
          <button
            className={filter === "public" ? "active-filter" : ""}
            onClick={() => handleFilterClick("public")}
          >
            공개
          </button>
          <button
            className={filter === "private" ? "active-filter" : ""}
            onClick={() => handleFilterClick("private")}
          >
            비공개
          </button>
        </div>
        <input
          type="text"
          placeholder="그룹명을 검색해 주세요"
          value={searchTerm}
          onChange={handleSearchChange}
          className="search-input"
        />
        <select
          value={sortOrder}
          onChange={handleSortChange}
          className="sort-select"
        >
          <option value="latest">최신순</option>
          <option value="mostLiked">공감순</option>
        </select>
        <button onClick={() => navigate("/create")} className="create-button">
          그룹 만들기
        </button>
      </div>
      <div className="group-list">
        {filteredGroups.length > 0 ? (
          filteredGroups.map((group, index) => (
            <div key={index} className="group-item">
              <img src={group.image} alt={group.title} />
              <h3>{group.title}</h3>
              <p>{group.description}</p>
            </div>
          ))
        ) : (
          <p>그룹이 없습니다.</p>
        )}
      </div>
    </div>
  );
};

export default Group;
