import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { v4 as uuidv4 } from "uuid";
import "./style.css";

const CreateGroupPage = () => {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [image, setImage] = useState(null); // 이미지 파일 상태
  const [isPrivate, setIsPrivate] = useState(false);
  const [password, setPassword] = useState("");
  const [isSuccess, setIsSuccess] = useState(null);
  const navigate = useNavigate();

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImage(file); // 파일 자체를 상태에 저장
    }
  };

  const uploadImage = async (file) => {
    const formData = new FormData();
    formData.append("image", file);

    const response = await fetch("/api/image", {
      method: "POST",
      body: formData,
    });

    if (response.ok) {
      const data = await response.json();
      return data.imageUrl; // 서버에서 반환한 이미지 URL
    } else {
      throw new Error("이미지 업로드에 실패했습니다.");
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // 그룹이 비공개인 경우 비밀번호가 입력되었는지 확인
    if (isPrivate && !password) {
      alert("비밀번호를 입력해 주세요.");
      return;
    }

    try {
      let imageUrl = "";
      if (image) {
        imageUrl = await uploadImage(image); // 이미지를 서버에 업로드하고 URL을 얻음
      }

      const newGroup = {
        title,
        description,
        imageUrl,
        isPrivate,
        password: isPrivate ? password : null, // 비공개 그룹일 경우에만 비밀번호 포함
      };

      const response = await fetch("/api/groups", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(newGroup),
      });

      if (response.ok) {
        setIsSuccess(true); // 성공 시 성공 상태 설정
      } else {
        setIsSuccess(false); // 실패 시 실패 상태 설정
      }
    } catch (error) {
      console.error("그룹 만들기 중 오류 발생:", error);
      setIsSuccess(false);
    }
  };

  return (
    <div>
      {isSuccess === null && (
        <div className="create-group-container">
          <h1 className="title">그룹 만들기</h1>
          <form onSubmit={handleSubmit} className="create-group-form">
            <div className="form-group">
              <label>그룹명</label>
              <input
                type="text"
                placeholder="그룹명을 입력해 주세요"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
              />
            </div>
            <div className="form-group">
              <label>대표 이미지</label>
              <div className="file-input-container">
                <input
                  type="file"
                  accept="image/*"
                  onChange={handleFileChange}
                  className="file-input"
                />
              </div>
            </div>
            <div className="form-group">
              <label>그룹 소개</label>
              <textarea
                placeholder="그룹을 소개해 주세요"
                value={description}
                onChange={(e) => setDescription(e.target.value)}
              />
            </div>
            <div className="form-group">
              <label>그룹 공개 선택</label>
              <div className="toggle-container">
                <label className="toggle-label">
                  공개
                  <input
                    type="checkbox"
                    checked={!isPrivate}
                    onChange={(e) => setIsPrivate(!e.target.checked)}
                    className="toggle-input"
                  />
                  <span className="toggle-slider"></span>
                </label>
              </div>
            </div>
            {isPrivate && (
              <div className="form-group">
                <label>비밀번호</label>
                <input
                  type="password"
                  placeholder="비밀번호를 입력해 주세요"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required={isPrivate} // 비공개인 경우 비밀번호 필수 입력
                />
              </div>
            )}
            <button type="submit" className="submit-button">
              만들기
            </button>
          </form>
        </div>
      )}
      {isSuccess !== null && (
        <div className={`message ${isSuccess ? "success" : "failure"}`}>
          <p>{isSuccess ? "그룹 만들기 성공" : "그룹 만들기 실패"}</p>
          <p className="details">
            {isSuccess
              ? "그룹이 성공적으로 등록되었습니다."
              : "그룹 등록에 실패했습니다."}
          </p>
          <button onClick={() => navigate("/")}>확인</button>
        </div>
      )}
    </div>
  );
};

export default CreateGroupPage;
