import React from "react";
import { useNavigate } from "react-router-dom";
import "./style.css";

const FilterBar = ({
  filter,
  setFilter,
  searchTerm,
  setSearchTerm,
  sortOrder,
  setSortOrder,
}) => {
  const navigate = useNavigate();

  const handleFilterClick = (filterType) => {
    setFilter(filterType);
    if (filterType === "public") {
      navigate("/public-group-list"); // 공개 그룹 경로로 이동
    } else if (filterType === "private") {
      navigate("/private-group-list"); // 비공개 그룹 경로로 이동
    }
  };

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleSortChange = (e) => {
    setSortOrder(e.target.value);
  };

  return (
    <div className="search-and-sort">
      <div className="filter-buttons">
        <button
          className={filter === "public" ? "active-filter" : ""}
          onClick={() => handleFilterClick("public")}
        >
          공개
        </button>
        <button
          className={filter === "private" ? "active-filter" : ""}
          onClick={() => handleFilterClick("private")}
        >
          비공개
        </button>
      </div>
      <input
        type="text"
        placeholder="그룹명을 검색해 주세요"
        value={searchTerm}
        onChange={handleSearchChange}
        className="search-input"
      />
      <select
        value={sortOrder}
        onChange={handleSortChange}
        className="sort-select"
      >
        <option value="latest">최신순</option>
        <option value="mostLiked">공감순</option>
      </select>
      <button onClick={() => navigate("/create")} className="create-button">
        그룹 만들기
      </button>
    </div>
  );
};

export default FilterBar;
