import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./style.css";

const Group = () => {
  const [groups, setGroups] = useState([]);
  const [filter, setFilter] = useState("public");
  const [searchTerm, setSearchTerm] = useState("");
  const [sortOrder, setSortOrder] = useState("latest");
  const navigate = useNavigate();

  useEffect(() => {
    // API를 통해 그룹 목록을 가져옵니다.
    fetchGroups();
  }, [filter, searchTerm, sortOrder]);

  const fetchGroups = async () => {
    try {
      const response = await axios.get("/api/groups", {
        params: {
          isPublic: filter === "public",
          keyword: searchTerm,
          sortBy: sortOrder,
        },
      });
      setGroups(response.data);
    } catch (error) {
      console.error("Failed to fetch groups:", error);
    }
  };

  const handleFilterClick = (filterType) => {
    setFilter(filterType);
  };

  const handleSearchChange = (e) => setSearchTerm(e.target.value);

  const handleSortChange = (e) => setSortOrder(e.target.value);

  const handleGroupClick = (group) => {
    navigate(
      group.isPrivate
        ? `/private-access/${group.id}`
        : `/group-details/${group.id}`
    );
  };

  const checkBadgeConditions = (group) => {
    let earnedBadges = [];

    if (group.memoryStreak >= 7) earnedBadges.push(1);
    if (group.memoryCount >= 20) earnedBadges.push(2);
    const oneYear = 365 * 24 * 60 * 60 * 1000;
    if (new Date() - new Date(group.createdAt) >= oneYear) earnedBadges.push(3);
    if (group.likeCount >= 10000) earnedBadges.push(4);
    if (group.memories?.some((memory) => memory.likeCount >= 10000))
      earnedBadges.push(5);

    return earnedBadges;
  };

  const filteredGroups = groups.map((group) => ({
    ...group,
    badgeIds: checkBadgeConditions(group),
  }));

  return (
    <div className="group-list-page">
      <div className="header-container">
        <div className="logo-container"></div>
        <div className="create-button-container">
          <button onClick={() => navigate("/create")} className="create-button">
            그룹 만들기
          </button>
        </div>
      </div>

      <div className="search-and-sort">
        <div className="filter-buttons">
          <button
            className={filter === "public" ? "active-filter" : ""}
            onClick={() => handleFilterClick("public")}
          >
            공개
          </button>
          <button
            className={filter === "private" ? "active-filter" : ""}
            onClick={() => handleFilterClick("private")}
          >
            비공개
          </button>
        </div>
        <input
          type="text"
          placeholder="그룹명을 검색해 주세요"
          value={searchTerm}
          onChange={handleSearchChange}
          className="search-input"
        />
        <select
          value={sortOrder}
          onChange={handleSortChange}
          className="sort-select"
        >
          <option value="latest">최신순</option>
          <option value="mostLiked">공감순</option>
          <option value="mostPosts">게시글 많은순</option>
          <option value="mostBadges">획득 배지순</option>
        </select>
      </div>

      <div className="group-list">
        {filteredGroups.length > 0 ? (
          filteredGroups.map((group, index) => (
            <div
              key={index}
              className="group-item"
              onClick={() => handleGroupClick(group)}
            >
              {group.image && (
                <img
                  src={group.image || "default-image-path.png"}
                  alt={group.title || "그룹 이미지"}
                />
              )}
              <div className="group-details">
                <span>D+{group.dDay}</span>
                <span>{group.isPrivate ? "비공개" : "공개"}</span>
              </div>
              <h3>{group.title}</h3>
              {group.description && <p>{group.description}</p>}
              <div className="group-stats">
                <div>
                  <span>획득 배지: {(group.badgeIds || []).length}</span>
                </div>
                <div>
                  <span>추억: {group.memoryCount}</span>
                </div>
                <div>
                  <span>공감수: {group.likeCount}</span>
                </div>
              </div>
            </div>
          ))
        ) : (
          <p>그룹이 없습니다.</p>
        )}
      </div>
    </div>
  );
};

export default Group;
