import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import GroupDetail from "..GroupDetail/index.js"; // GroupDetail 컴포넌트를 가져옵니다.
import "./style.css";

const Group = ({ groups = [] }) => {
  const [filter, setFilter] = useState("public");
  const [searchTerm, setSearchTerm] = useState("");
  const [sortOrder, setSortOrder] = useState("latest");
  const [groupList, setGroupList] = useState(groups); // 그룹 목록 상태 관리
  const navigate = useNavigate();

  const handleFilterClick = (filterType) => {
    setFilter(filterType);
    if (filterType === "public") {
      navigate("/public-group-list");
    } else if (filterType === "private") {
      navigate("/private-group-list");
    }
  };

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleSortChange = (e) => {
    setSortOrder(e.target.value);
  };

  const handleGroupClick = (group) => {
    if (group.isPrivate) {
      navigate(`/private-access/${group.id}`);
    } else {
      navigate(`/group-details/${group.id}`);
    }
  };

  const handleDeleteGroup = (groupId) => {
    const updatedGroups = groupList.filter((group) => group.id !== groupId);
    setGroupList(updatedGroups); // 그룹 목록을 업데이트합니다.
  };

  const filteredGroups = groupList
    .filter((group) => {
      return filter === "public" ? !group.isPrivate : group.isPrivate;
    })
    .filter((group) =>
      group.title?.toLowerCase().includes(searchTerm.toLowerCase())
    )
    .sort((a, b) => {
      if (sortOrder === "latest") {
        return new Date(b.createdAt) - new Date(a.createdAt);
      } else if (sortOrder === "mostLiked") {
        return b.likeCount - a.likeCount;
      } else if (sortOrder === "mostPosts") {
        return b.memoryCount - a.memoryCount;
      } else if (sortOrder === "mostBadges") {
        return b.badgeCount - a.badgeCount;
      }
      return 0;
    });

  return (
    <div className="group-list-page">
      <div className="header-container">
        <div className="logo-container"></div>
        <div className="create-button-container">
          <button onClick={() => navigate("/create")} className="create-button">
            그룹 만들기
          </button>
        </div>
      </div>
      <div className="search-and-sort">
        <div className="filter-buttons">
          <button
            className={filter === "public" ? "active-filter" : ""}
            onClick={() => handleFilterClick("public")}
          >
            공개
          </button>
          <button
            className={filter === "private" ? "active-filter" : ""}
            onClick={() => handleFilterClick("private")}
          >
            비공개
          </button>
        </div>
        <input
          type="text"
          placeholder="그룹명을 검색해 주세요"
          value={searchTerm}
          onChange={handleSearchChange}
          className="search-input"
        />
        <select
          value={sortOrder}
          onChange={handleSortChange}
          className="sort-select"
        >
          <option value="latest">최신순</option>
          <option value="mostLiked">공감순</option>
          <option value="mostPosts">게시글 많은순</option>
          <option value="mostBadges">획득 배지순</option>
        </select>
      </div>
      <div className="group-list">
        {filteredGroups.length > 0 ? (
          filteredGroups.map((group, index) => (
            <div
              key={index}
              className={`group-item ${!group.image ? "no-image" : ""} ${
                !group.description ? "no-description" : ""
              }`}
              onClick={() => handleGroupClick(group)}
              style={{ cursor: "pointer" }}
            >
              {group.image && (
                <img
                  src={group.image || "default-image-path.png"}
                  alt={group.title || "그룹 이미지"}
                />
              )}
              <div className="group-details">
                <span>D+{group.dDay}</span>
                <span>{group.isPrivate ? "비공개" : "공개"}</span>
              </div>
              <h3>{group.title}</h3>
              {group.description && <p>{group.description}</p>}
              <div className="group-stats">
                <div>
                  <span>획득 배지: {group.badgeCount}</span>
                </div>
                <div>
                  <span>추억: {group.memoryCount}</span>
                </div>
                <div>
                  <span>공감수: {group.likeCount}</span>
                </div>
              </div>
              {/* GroupDetail 컴포넌트에 삭제 함수 전달 */}
              <GroupDetail group={group} onDeleteGroup={handleDeleteGroup} />
            </div>
          ))
        ) : (
          <p>그룹이 없습니다.</p>
        )}
      </div>
    </div>
  );
};

export default Group;
